package tlb

import (
	"encoding/hex"
	"github.com/xssnick/tonutils-go/tvm/cell"
	"testing"
)

func TestTransaction_String(t *testing.T) {
	txData, _ := hex.DecodeString("")
	txCell, err := cell.FromBOC(txData)
	if err != nil {
		t.Fatal(err)
	}
	var tx Transaction
	err = LoadFromCell(&tx, txCell.BeginParse())
	if err != nil {
		t.Fatal(err)
	}
	tx.Hash = txCell.Hash()

	if tx.String() != "LT: 35290576000004, In: 2.1 TON, From EQANrdEh_lJ10saEhhft5-qjQrOAwSArR244rXwrGwI8_V1l, Out: 2.084509999 TON, To [EQD3cvYfoK3Vd2WhOm1cpnYt6ouh9_fjDtC4-NQiutpgen0U]" {
		t.Errorf("another string expected")
	}
}

func TestTransaction_Dump(t *testing.T) {
	txData, _ := hex.DecodeString("")
	txCell, err := cell.FromBOC(txData)
	if err != nil {
		t.Fatal(err)
	}
	var tx Transaction
	err = LoadFromCell(&tx, txCell.BeginParse())
	if err != nil {
		t.Fatal(err)
	}
	tx.Hash = txCell.Hash()
	tx.Dump()
}
