func TestSmallBOC(t *testing.T) {
	str := "b5ee9c72010101010002000000"

	c := BeginCell().EndCell()

	boc := c.ToBOCWithFlags(false)

	if str != hex.EncodeToString(boc) {
		t.Log(str)
		t.Log(hex.EncodeToString(boc))
		t.Fatal("boc not same")
	}
}

